import React, { Component } from "react";
import logo from "./logo.svg";
import "./App.css";

import axios from 'axios';

class App extends Component {
    constructor(props) {
        super(props);
        this.state = { 
          apiResponse: "hi there", 
          valveState: {
            timer: 3000, //ms
            valve1: {
              status: false
            },
            valve2: {
              status: false
          }
      } };
    }

    callAPI() {
      console.log(this.state);
      console.log(this.state.valveState.valve1.status);
      console.log("api called")
        fetch("http://localhost:9000/")
            .then(res => res.text())
            .then(res => this.setState({ apiResponse: res }))
            .catch(err => err);
    }

    componentDidMount() {
        this.callAPI();
    }



    handleButtonClick(e, valveName) {
      e.preventDefault();
      console.log(valveName);

      axios.get(`http://localhost:9000/${valveName}/on`)
      .then(res => console.log(res));
      this.callAPI();
    }

    render() {
        return (
            <div className="App">
                <header className="App-header">
                <img src={logo} className="App-logo" alt="logo" />
                    <h1 className="App-title">Welcome to the Control System</h1>    

                </header>
                <h2 className = "App-intro">{this.state.apiResponse}</h2>
                  <button type="button" className="btn" onClick={e => this.handleButtonClick(e, "valve1")} >Valve 1</button>
                  <button type="button" className="btn" onClick={e => this.handleButtonClick(e, "valve2")}>Valve 2</button>
                <footer className="footer">Created 2020, Patrik Mackerle, version 0.1 ALFA</footer>
            </div>
        );
    }
}

export default App;
