import React, { Component } from "react";
import logo from "./logo.svg";
import "./App.css";

import axios from 'axios';

class App extends Component {  

    state = {
          serverConnected: false,
          apiResponse: "Hi there, server not connected....", 
          };


    callAPI() {
      console.log("api called")
        fetch("http://localhost:9000/")            
            .then(res => res.text())
            .then(res => {
              let newState = JSON.parse(res);
              this.setState({...newState, apiResponse: "Server Connected", serverConnected: true})

            })
            .catch(err => err);
    }

    componentDidMount() {
        this.callAPI();
    }

    updateAPI() {
      console.log("updateing api")
      fetch("http://localhost:9000/")
      .then(res => res.text())
      .then(res => {
        let newState = JSON.parse(res);
        this.setState({...newState})        
      })
      .catch(err => err);
    }



    handleButtonClick(e, valveName) {
      e.preventDefault();

      axios.get(`http://localhost:9000/${valveName}`)
      .then(res => console.log(res))
      .then(this.updateAPI()); //vymyslet neco lepsiho, co to zase neupdatuje cely :) 
      console.log(this.state.valve1)
    }

    textChange(valveName) {
    }


    render() {
      let text = " is OFF";
      if (this.state.serverConnected) {
        console.log("hihihih")
        if (this.state.valve1.status ) {
          console.log(this.state.valve1.status)
          text = " is ON"
        }

      }

      console.log(this.state.valve1);
        return (
            <div className="App">
                <header className="App-header">
                <img src={logo} className="App-logo" alt="logo" />
                    <h1 className="App-title">Welcome to the Control System</h1>    

                </header>
                <h2 className = "App-intro">{this.state.apiResponse}</h2>
                <div className= "btn-group">                  
        <button type="button" className="btn" id="valve1"  onClick={e => this.handleButtonClick(e, "valve1")} >Valve 1 {text}</button>              
                   <button type="button" className="btn" onClick={e => this.handleButtonClick(e, "valve2")}>Valve 2</button>
                </div>

                <footer className="footer">Created 2020, Patrik Mackerle, version 0.1 ALFA</footer>
            </div>
        );
    }
}

export default App;
